import * as Vite from 'vite';
import * as Stylelint from 'stylelint';

type FilterPattern = string | string[];
interface StylelintPluginOptions extends Stylelint.LinterOptions {
    cache: boolean;
    cacheLocation: string;
    include: FilterPattern;
    exclude: FilterPattern;
    stylelintPath: string;
    formatter: Stylelint.FormatterType | Stylelint.Formatter;
    lintOnStart: boolean;
    chokidar: boolean;
    emitError: boolean;
    emitErrorAsWarning: boolean;
    emitWarning: boolean;
    emitWarningAsError: boolean;
}
interface StylelintPluginUserOptions extends Partial<StylelintPluginOptions> {
    dev?: boolean;
    build?: boolean;
}

declare function StylelintPlugin(userOptions?: StylelintPluginUserOptions): Vite.Plugin;

export { StylelintPlugin as default };
